func longestValidParentheses(s string) int {
	best := 0
	for index := range s {
		helper(s[index:], 0, 1, &best)
	}
	return best
}

func helper(s string, toClose int, length int, best *int) {
	if len(s) == 0 || toClose > len(s) {
		return
	}
	if string(s[0]) == "(" {
		helper(s[1:], toClose+1, length+1, best)
	} else {
		if toClose-1 == 0 {
			if length > *best {
				*best = length
			}
		}
		if toClose > 0 {
			helper(s[1:], toClose-1, length+1, best)
		}
	}
}

